    "interestingInput/test_232625801673426796229499089961237209913-fixed.dot",
    "interestingInput/test_232625801673426796229499089961237209913-small-fixed.dot",
    "interestingInput/test_36181720087097266064494270500422776390-fixed.dot",
    "interestingInput/test_36181720087097266064494270500422776390-small-fixed.dot",
    "interestingInput/test_273338121150092584181509196004485960627-fixed.dot",
    "interestingInput/test_273338121150092584181509196004485960627-small-fixed.dot",
}
Util.testFiles(files, p)


